$(document).ready(function(){$("a").on("click",function(e){if(""!==this.hash){e.preventDefault();var o=this.hash;$("html, body").animate({scrollTop:$(o).offset().top},800,function(){window.location.hash=o})}})});const sectionNames=["header","aboutus","ourgoal","methods-to-acheive-our-goal","ngos"],sec1=$("#header").offset().top-50,sec2=$("#aboutus").offset().top-50,sec3=$("#ourgoal").offset().top-50,sec4=$("#methods-to-acheive-our-goal").offset().top-50,sec5=$("#ngos").offset().top-50,sections=[sec1,sec2,sec3,sec4,sec5];$(window).scroll(function(){const e=$(window).scrollTop();let o=0;for(const t of sections){if(e>=t){for(let e=0;e<sectionNames.length;e++)document.querySelectorAll(".header__navigation-item").forEach(e=>{e.classList.remove("header__navigation-item--active")});document.querySelector(`.header__navigation-item[href="#${sectionNames[o]}"]`).classList.add("header__navigation-item--active")}o++}});